import React, { useState } from "react";
import productServices from "../../services/product-services/product-services";
import { redirect, useNavigate } from "react-router-dom";


export default function ProductEdit() {
  const navigate = useNavigate();
  const [product, setProduct] = useState({
    id: null,
    nome: "",
    preco: null
  });


  const handleSubmit = async (event) => {
    event.preventDefault();
    productServices.create(product).then((response) => {
      if (response.status === 200) {
        navigate('/');
      }else {
        Swal.fire({
          title: "Erro ao criar produto:!",
          timer: 900,
          icon: "error"
        });
      }
    });
  }

  return (
    <form onSubmit={handleSubmit}>
      {console.log(product)}
      <div>
        <label htmlFor="nome">Nome:</label>
        <input
          type="text"
          id="nome"
          value={product.nome}
          onChange={(e) => setProduct({ ...product, nome: e.target.value })}
        />
      </div>
      <div>
        <label htmlFor="preco">Pre√ßo:</label>
        <input
          type="number"
          id="preco"
          value={product.preco}
          onChange={(e) => setProduct({ ...product, preco: e.target.value })}
        />
      </div>
      <button type="submit">Adicionar Produto</button>
    </form>
  );
}
