import React, { useEffect, useState } from "react";
import productServices from "../../services/product-services/product-services";
import { Link } from "react-router-dom";

export default function ProductList() {
  const [products, setProducts] = useState([])

  useEffect(() => {
    const products = productServices.getAll();
    setProducts(products)
  }, [])


  const handleDelete = async (productId) => {

  };

  return (
    <main>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Categoria</th>
            <th>Nome</th>
            <th>Preço</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {products.length > 0 ? (
            products.map(product => (
              <tr key={product.id}>
                <td>{product.id}</td>
                <td>{product.categoria?.nome || '-'}</td>
                <td>{product.nome}</td>
                <td>{product.preco}</td>
                <td>
                  <Link to={`/products/edit/${product.id}`}>
                    <button className="action-button">Editar</button>
                  </Link>
                  <button className="action-button" onClick={() => handleDelete(product.id)}>Excluir</button>
                </td>
              </tr>
            ))
          ) : (
            <div style={{textAlign: "center"}}>
              <td colSpan="5">Vazio</td>
            </div>
          )}
        </tbody>
      </table>
      <div class="button-container">
        <Link to={`products/new`}>
          <a class="button">Adicionar Novo Produto</a>
        </Link>
      </div>
    </main >
  );
}
